mapper.select:
SELECT id, email, nickName, password
FROM member
WHERE email=#{email}

SELECT EXISTS(
                SELECT id
                FROM member
                WHERE email=#{email}
                LIMIT 1
           )

SELECT EXISTS(
               SELECT id
               FROM member
               WHERE nickName=#{nickName}
               LIMIT 1
           )

SELECT id
FROM member
WHERE email=#{email}

SELECT salt
FROM member
WHERE id=#{id}

mapper.update:
UPDATE member
SET salt=#{salt}
WHERE id=#{id}

UPDATE member
SET isDeleted=1
WHERE id=#{id}
